--==================== -- Guest666 Controls GUI LocalScript --==================== local TweenService = game:GetService("TweenService") local Players = game:GetService("Players") local TextChatService = game:GetService("TextChatService") local LocalPlayer = Players.LocalPlayer local PlayerGui = LocalPlayer:WaitForChild("PlayerGui") local UserInputService = game:GetService("UserInputService") local RunService = game:GetService("RunService") local Workspace = game:GetService("Workspace") --==================== -- CHAT HELPER --==================== local function saySomething(message) local generalChannel = TextChatService:WaitForChild("TextChannels"):WaitForChild("RBXGeneral") generalChannel:SendAsync(message) end --==================== -- CREATE MAIN GUI --==================== local ScreenGui = Instance.new("ScreenGui") ScreenGui.ResetOnSpawn = false ScreenGui.Parent = PlayerGui -- Main Panel local Frame = Instance.new("Frame") Frame.Size = UDim2.new(0, 340, 0, 450) Frame.Position = UDim2.new(0.03,0,0.2,0) Frame.BackgroundColor3 = Color3.fromRGB(50,0,0) Frame.BorderSizePixel = 0 Frame.Parent = ScreenGui local frameCorner = Instance.new("UICorner", Frame) frameCorner.CornerRadius = UDim.new(0,15) -- Gradient local frameGrad = Instance.new("UIGradient", Frame) frameGrad.Color = ColorSequence.new({ColorSequenceKeypoint.new(0,Color3.fromRGB(80,0,0)), ColorSequenceKeypoint.new(1,Color3.fromRGB(150,0,0))}) -- Title local Title = Instance.new("TextLabel") Title.Size = UDim2.new(1,0,0,40) Title.Position = UDim2.new(0,0,0,0) Title.BackgroundTransparency = 1 Title.Text = "Guest666 Controls" Title.Font = Enum.Font.GothamBlack Title.TextSize = 22 Title.TextColor3 = Color3.fromRGB(255,255,255) Title.Parent = Frame -- Close button local CloseBtn = Instance.new("TextButton") CloseBtn.Size = UDim2.new(0,30,0,30) CloseBtn.Position = UDim2.new(1,-35,0,5) CloseBtn.BackgroundColor3 = Color3.fromRGB(255,0,0) CloseBtn.Text = "X" CloseBtn.Font = Enum.Font.GothamBold CloseBtn.TextColor3 = Color3.fromRGB(255,255,255) CloseBtn.TextSize = 20 CloseBtn.Parent = Frame Instance.new("UICorner", CloseBtn).CornerRadius = UDim.new(0,6) -- Scrollable Button Panel local Scroll = Instance.new("ScrollingFrame") Scroll.Size = UDim2.new(1,-20,1,-60) Scroll.Position = UDim2.new(0,10,0,50) Scroll.ScrollBarThickness = 6 Scroll.BackgroundTransparency = 0.2 Scroll.BackgroundColor3 = Color3.fromRGB(80,0,0) Scroll.Parent = Frame local scrollCorner = Instance.new("UICorner", Scroll) scrollCorner.CornerRadius = UDim.new(0,12) -- Button Helper local BtnY = 0 local BtnHeight = 40 local function makeButton(text, callback) local btn = Instance.new("TextButton") btn.Size = UDim2.new(1,-10,0,BtnHeight) btn.Position = UDim2.new(0,5,0,BtnY) btn.Text = text btn.BackgroundColor3 = Color3.fromRGB(200,0,0) btn.TextColor3 = Color3.fromRGB(255,255,255) btn.Font = Enum.Font.GothamBold btn.TextSize = 16 btn.Parent = Scroll local c = Instance.new("UICorner") c.CornerRadius = UDim.new(0,10) c.Parent = btn -- Hover effect btn.MouseEnter:Connect(function() TweenService:Create(btn,TweenInfo.new(0.15),{BackgroundColor3=Color3.fromRGB(255,50,50)}):Play() end) btn.MouseLeave:Connect(function() TweenService:Create(btn,TweenInfo.new(0.15),{BackgroundColor3=Color3.fromRGB(200,0,0)}):Play() end) btn.MouseButton1Click:Connect(callback) BtnY = BtnY + BtnHeight + 6 end --==================== -- DRAGGING GUI --==================== local dragging, dragInput, mousePos, framePos = false,nil,nil,nil local function update(input) local delta = input.Position - mousePos Frame.Position = UDim2.new(framePos.X.Scale, framePos.X.Offset + delta.X, framePos.Y.Scale, framePos.Y.Offset + delta.Y) end Frame.InputBegan:Connect(function(input) if input.UserInputType == Enum.UserInputType.MouseButton1 then dragging = true mousePos = input.Position framePos = Frame.Position input.Changed:Connect(function() if input.UserInputState == Enum.UserInputState.End then dragging = false end end) end end) Frame.InputChanged:Connect(function(input) if input.UserInputType == Enum.UserInputType.MouseMovement then dragInput = input end end) UserInputService.InputChanged:Connect(function(input) if input == dragInput and dragging then update(input) end end) -- Close/Open Buttons local OpenBtn = Instance.new("TextButton") OpenBtn.Size = UDim2.new(0,100,0,40) OpenBtn.Position = Frame.Position OpenBtn.BackgroundColor3 = Color3.fromRGB(180,0,0) OpenBtn.Text = "Open" OpenBtn.Font = Enum.Font.GothamBold OpenBtn.TextColor3 = Color3.fromRGB(255,255,255) OpenBtn.TextSize = 18 OpenBtn.Visible = false OpenBtn.Parent = ScreenGui Instance.new("UICorner", OpenBtn).CornerRadius = UDim.new(0,10) CloseBtn.MouseButton1Click:Connect(function() Frame.Visible = false OpenBtn.Position = Frame.Position OpenBtn.Visible = true end) OpenBtn.MouseButton1Click:Connect(function() Frame.Visible = true OpenBtn.Visible = false end) --==================== -- AUTO COMMANDS ON LOAD --==================== task.spawn(function() task.wait(1) saySomething(";fog") saySomething(";fogcolor red") saySomething(";fire me") end) --==================== -- PHASE UNDER MAP & RISE --==================== local character = LocalPlayer.Character or LocalPlayer.CharacterAdded:Wait() local hrp = character:WaitForChild("HumanoidRootPart") local originalY = hrp.Position.Y -- Disable collisions for all parts for _, part in ipairs(character:GetDescendants()) do if part:IsA("BasePart") then part.CanCollide = false end end -- Teleport under map 100 studs hrp.CFrame = hrp.CFrame + Vector3.new(0,-100,0) -- Slowly rise back to original Y local function riseUp(speed) speed = speed or 0.5 while hrp.Position.Y < originalY do hrp.CFrame = hrp.CFrame + Vector3.new(0,speed,0) task.wait(0.05) end end task.spawn(function() riseUp(0.5) end) --==================== -- COMMAND BUTTONS --==================== makeButton("ðŸŽµ Return Main Music", function() saySomething(";music 17647322226") end) makeButton("ðŸ˜± Jumpscare 1", function() saySomething(";music 18911896588") end) makeButton("ðŸ˜± Jumpscare 2", function() saySomething(";music 8484439709") end) makeButton("ðŸ˜± Jumpscare 3", function() saySomething(";music 5943190023") end) makeButton("ðŸŒ™ Time â†’ NIGHT", function() saySomething(";time night") end) makeButton("â˜€ï¸ Time â†’ DAY", function() saySomething(";time day") end) makeButton("ðŸ”Š Volume = 666", function() saySomething(";vol 666") end) makeButton("ðŸŒ«ï¸ Enable Fog", function() saySomething(";fog") end) makeButton("ðŸ”´ Fog Color RED", function() saySomething(";fogcolor red") end) makeButton("âš ï¸ Corrupt Broadcast", function() saySomething(";Mr attention sever this sever is being curropted") end) makeButton("ðŸ› ï¸ Building Tools", function() saySomething(";buildingTools me") end) makeButton("ðŸ—‘ï¸ Delete Map", function() saySomething(";buildingTools me") end) --==================== -- WARNING BUTTON --==================== makeButton("ðŸš¨ Warning / TP All", function() saySomething(";Mr fellow players leave now before this sever becomes corrupt and hacks your account this is for your safety") task.delay(20, function() -- Teleport you 5 million studs away hrp.CFrame = hrp.CFrame + Vector3.new(0,0,5000000) -- Teleport all players to you for _, p in ipairs(Players:GetPlayers()) do if p ~= LocalPlayer and p.Character and p.Character:FindFirstChild("HumanoidRootPart") then p.Character.HumanoidRootPart.CFrame = hrp.CFrame end end end) end)
